    <style>

        @keyframes fade-in-out {
            0% { opacity: 0; transform: rotate(90deg); }
            50% { opacity: 1; }
            100% { opacity: 0; transform: rotate(0); }
        }

        @keyframes rotate1 {
            0% { transform: rotate3d(0, 1, 0, 180deg) scale(1); }
            100% { transform: rotate3d(0, 1, 0, 0) scale(0); }
        }

        @keyframes rotate2 {
            0% { transform: rotate3d(1, 0, 0, -360deg) scale(1); }
            100% { transform: rotate3d(1, 0, 0, 0) scale(0); }
        }

        .star-loader-container {
            position: fixed;
            top: 0;
            left: 0;
            z-index: 1000;
            display: flex;
            align-items: center;
            justify-content: center;
            width: 100%;
            height: 100%;
            background-color: rgb(250,250,250);
            opacity: 1;
        }

        .star-loader-logo {
            width: 300px;

        }

        .star-loader {
            position: absolute;
            top: calc(50% - 72px);
            left: calc(50% - 68px);
            fill: #000000;
            transform-origin: 40% 90%;
            animation: fade-in-out 1.5s infinite;
        }
        .star-loader .large {
            transform-origin: 50% 50%;
            transform-style: preserve3d;
            animation: rotate1 1.5s infinite;
        }
        .star-loader .large-2 {
            transform: rotate(45deg) scale(0.8);
            transform-origin: 33px 33px;
        }

        .star-loader .small {
            transform-origin: 50% 50%;
            transform-style: preserve3d;
            animation: rotate2 1.5s infinite;
        }
        .star-loader .small-1 {
            transform: rotate(45deg) scale(0.667);
            transform-origin: 33px 33px;
        }

        body.loaded .star-loader-container {
            visibility: hidden;
            pointer-events: none;
            opacity: 0;
            transition: visibility 0s 0.5s linear, opacity .5s ease;
        }
    </style>
</head>
