    <title>Magic Desk</title>

    <link rel="stylesheet" href="{{ url(mix('css/app.css')) }}">

    <style>

        .app-loader {
            position: fixed;
            top: 0;
            left: 0;
            z-index: 1000;
            display: flex;
            align-items: center;
            justify-content: center;
            width: 100%;
            height: 100%;
            background-color: rgb(250,250,250);
            opacity: 1;
        }

        .app-loader__container {
            display: flex;
            flex-direction: column;
            align-items: center;
        }

        .app-loader__logo {
            width: 100px;
            height: 100px;
            padding: 15px;
            animation: rotate 2s linear infinite;
            transform-origin: center center;
        }

        .app-loader__title {
            height: 70px;
            padding: 15px;
        }

        @keyframes rotate {
            100% {
                transform: rotate3d(0,0,1,360deg);
            }
        }

        body.loaded .app-loader {
            visibility: hidden;
            pointer-events: none;
            opacity: 0;
            transition: visibility 1s 2s linear, opacity 1s ease;
            transition-delay: .5s
        }

        body.loaded .app-loader__container {
            opacity: 0;
            transition: transform .5s ease, opacity 0.5s ease;
            transform: translate3d(0,-10px,0);
        }
    </style>
</head>
